<script type="text/javascript">

    $(document).ready(function () {
        //有资讯优先显示资讯
        if($('.box2').length > 0){
            $('.article_tip').addClass("active");
            $('.box1').hide();
            $('.box2').show();


        }else if ($('.box1').length > 0) {
            $('.box1').show();
            $('.package_tip').addClass("active");
            $('.box2').hide();
        }else{
            $('.box2').hide();
            $('.box1').hide();
        }

        //tab切换
        $(".tabs li").click(function () {
            var liindex = $(".tabs li").index(this);
            $(".tabs").find("li").removeClass("active");
            $(this).addClass("active");
            $(".tab_cont").eq(liindex).show().siblings(".tab_cont").hide();
        });

        //描述
        var slideHeight = 85; // px
        var defHeight = $('.info').height();
        if (defHeight >= slideHeight) {
            $('.info').css('height', slideHeight + 'px');
            $('.info').css('overflow', 'hidden');
            $('.readmore').append('<a class="more" href="javascript:;" style="text-align:center;">查 看 更 多</a>');
            $('.readmore a').click(function () {
                var curHeight = $('.info').height();
                if (curHeight == slideHeight) {
                    $('.info').animate({
                        height: defHeight
                    }, "normal");
                    $('.readmore a').html('隐 藏');
                    $('.gradient').fadeOut();
                } else {
                    $('.info').animate({
                        height: slideHeight
                    }, "normal");
                    $('.readmore a').html('查 看 更 多');
                    $('.info').fadeIn();
                }
                return false;
            });
        }

    });



 


    function fengwanpay(name, price, ext, sign) {
        BTGameSDK.pay_gm(name, price, ext, sign);
    }

//维护
        layer.msg('此款游戏不参与包站', {icon: 5});


