var u = $(this).parents('.gift').find('.part2');
u.is(':hidden') == false ? u.slideUp(300) : u.slideDown(300);
});
})
  
  $(function() {
      $("img.lazy").lazyload({effect: "fadeIn"});
  });
</script>
